resources.string:
       
TestLabel = CreateLabel();
TestLabel:SetSize(450, 200);
TestLabel:SetPosition(10, 10);
TestLabel:SetAnchor(\"ANCHOR_BOTTOM_LEFT\", \"ANCHOR_BOTTOM_LEFT\");
TestLabel:SetText(\"testtest testtest test test test\");
TestLabel:Show();

last = 0;
function OnGlobalEvent(event, arg1)
	local x, y = GetPlayerPosition();
	TestLabel:SetText(x .. \"\\n\" .. y);

	local t = os.clock();
	FpsLabel:SetText(1/(t-last));
	last = t;
end

SetEventHandler(OnGlobalEvent);
RegisterEvent(\"EVENT_GLOBAL_LOG\");


FpsLabel = CreateLabel();
FpsLabel:SetSize(84, 44);
FpsLabel:SetPosition(10, -10);
FpsLabel:SetAnchor(\"ANCHOR_TOP_LEFT\", \"ANCHOR_TOP_LEFT\");
FpsLabel:SetText(\"60\");
FpsLabel:Show();
